Script.ReportStart:
int type = _dataObject.Set1[0].Type.Value;

if (_dataObject.PrintStamp.Value && _dataObject.Set1[0].DebetCurrencyID.ValueN == 1 && _dataObject.Set1[0].CreditCurrencyID.ValueN == 1 && 
 (type == 1 || type == 2 || type == 5))
{
	ReportHelper.MakeStamp(StampOffice, _environment, _dataObject.Set1[0].ValueDate.Value, false);
}
else
{
	StampOffice.Visible = false;
}

Section.Format:
Detail.Visible =_currentRow.DebetCurrencyID.ValueN == 1 && _currentRow.CreditCurrencyID.ValueN == 1 && 
 (_currentRow.Type.Value == 1 || _currentRow.Type.Value == 2 || _currentRow.Type.Value == 5);

//платежка
//ИНН филиала проводки совпадает с ИНН плательщика - по договоренности с Акманаевой не заполняем поле 'Дата поступления' и 'Списано со счета плат.'
// то же самое для случая IsSelfPaymentOrder (собственное платежное поручение)
BranchInfo transactionBranch = OrgStructureServerCache.Instance.GetBranchByID(_dataObject.Set1[0].BranchID.Value);
if (_dataObject.Set2[0].IsSelfPaymentOrder.Value || !_dataObject.Set2[0].PayerINN.IsNull && transactionBranch.INN.Equals(_dataObject.Set2[0].PayerINN.Value))
{	
	_paymentReceiveDateTextBox.Text = String.Empty;
	_valueDateTextBox.Text = String.Empty;
}

Script.ReportStart:
int type = _dataObject.Set1[0].Type.Value;

if (_dataObject.PrintStamp.Value && _dataObject.Set1[0].DebetCurrencyID.ValueN == 1 && _dataObject.Set1[0].CreditCurrencyID.ValueN == 1 && 
 (type == 1 || type == 2 || type == 5))
{
	ReportHelper.MakeStamp(StampOffice, _environment, _dataObject.Set1[0].ValueDate.Value, false);
}
else
{
	StampOffice.Visible = false;
}

Section.Format:
//инкассовое поручение
//ИНН филиала проводки совпадает с ИНН получателя - по договоренности с Акманаевой не заполняем поле 'Дата поступления' и 'Списано со счета плат.'
BranchInfo transactionBranch = OrgStructureServerCache.Instance.GetBranchByID(_dataObject.Set1[0].BranchID.Value);
if (!_dataObject.Set2[0].ReceiverINN.IsNull && transactionBranch.INN.Equals(_dataObject.Set2[0].ReceiverINN.Value))
{
	_paymentReceiveDateTextBox.Text = String.Empty;
	_valueDateTextBox.Text = String.Empty;
}

Script.ReportStart:
int type = _dataObject.Set5[0].Type.Value;

if (_dataObject.PrintStamp.Value && (type == 1 || type == 2 || type == 3))
{
	ReportHelper.MakeStamp(StampOffice, _environment, _dataObject.Set5[0].ValueDate.Value, false);
}
else
{
	StampOffice.Visible = false;
}

